mapper.select:
select
  id, username, password, salt, create_time, update_time
from demo.user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and salt = #{salt}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.update:
update demo.user


        username = #{username},


        password = #{password},


        salt = #{salt}


where id = #{id}

mapper.select:
select count(id) from demo.user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and salt = #{salt}


        and create_time = #{createTime}


        and update_time = #{updateTime}
